mapper.insert:
insert into chatroom (
name,
bno
) values (
#{name},
#{bno}
)

mapper.select:
SELECT *,
CASE
WHEN EXISTS (
SELECT *
FROM chatmessage
WHERE chatRoomNo = cr.chatRoomNo AND checked = '0' AND senderNo != #{memberNo}
) THEN 'F'
ELSE 'T'
END AS flag
FROM chatroom AS cr
WHERE cr.chatRoomNo IN (
SELECT chatRoomNo
FROM chatmessage
WHERE receiverNo = #{memberNo} OR senderNo = #{memberNo}
)
GROUP BY cr.chatRoomNo;

mapper.update:
UPDATE chatmessage
SET checked=1
WHERE senderNo != #{senderNo} and chatRoomNo = #{chatRoomNo};
